import React, {Component} from 'react';

class News extends Component {

    //State
    state = {
        news: [
            {
                title: 'News 1',
                description: 'Description news 1'
            },
            {
                title: 'News 2',
                description: 'Description news 2'
            }
        ],
    }

    //Methods


    render() {
        return (
            <div className='news-wrapper'>
                <h3>{this.state.news[0].title}</h3>
                <p>{this.state.news[0].description}</p>
                <button onClick={this.props.onChangeTitleNews}>Change title news</button> {/*Принимаем метод в качестве свойства*/}
            </div>
        )
    }
}

export default News;
