import React, {Component} from 'react';
import './App.css';
import Car from './components/Car/Car';
import News from "./components/News/News";

class App extends Component {

    //State
    state = {
        pageTitle: 'Title page of React'
    }

    //Methods
    handlerChangeTitleNews = () => {
        console.log('!!!!!')
    };

    render() {
        const divStyle = {
            'color': 'red'
        }
        return (
            <div className="App">
                <div style={divStyle}>
                    <p style={{fontSize: 20}}>HELLO</p>
                    <Car name={'BMW'} year={2019}/> {/*Передача параметров*/}
                    <h1>{this.state.pageTitle}</h1>
                    <News onChangeTitleNews={this.handlerChangeTitleNews.bind(this)}/> {/*Передаем метод в качестве свойства*/}
                    <News/>
                </div>
            </div>
        );
    }

}

export default App;
